/*Header*/
@import "../base/variables";
@import "../base/mixin";
@import "../base/listmaps";


.portada{
    width: 100%;
    height: map-get($maps, alto);
    display: grid;
    grid-template-columns: repeat(2 , 1fr);
    grid-area: "dark" "golden";

    .uno{
        grid-area: "dark";   
        background-color: map-get($maps , dark);  
        color: #B69121;
        font-family: "sansBoldItalic";
        display: $df;
        justify-content:flex-end;
        align-items: $ai;
        width: map-get($maps, ancho);
        height: map-get($maps, alto);
        @include fonts;
    }

    span{
        grid-area: "golden";
        background-color: #B69121;
        color: map-get($maps , dark);
        padding: auto;
        height: map-get($maps , alto);
        width: 50vw;
        display: $df;
        justify-content:flex-start;
        align-items: $ai;
        padding-top: 5%;
        padding-left: 0.5%;
        margin-left: 0.5%;
        @include fonts;
    }

}


/*navbar*/

.navbar-dark {

    .navbar-toggler {
    padding-left: 40px;
    }
}

#navbarNav{
    display: grid;
    grid-template-columns: 1fr;
    grid-template-rows: 1fr;
    width: 100%;
    grid-template-areas: "nav" ;

        .navbar-nav{
            background-image: linear-gradient(to right,#000000,#171717,#171717,#000000);
            width: 100%;
            padding: 2rem;
            grid-area: "nav";
        }

    ul{
        width: 70%;
        padding:1rem ;
        margin: 0 auto;
        display: $df;
        justify-content: space-evenly;
        align-items: $ai; 

            li{

                a{
                    color: #ffffff;
                    text-transform: capitalize;
                    font-style: oblique;
                    font-size: $font-s;
                    font-weight: bolder; 
                        &:hover{
                            color: #4C5B5C;
                            font-style:italic;
                        } 
                }
            }
    }   


}

.caja{
    position: absolute;
    left: 30px;
    top: 0px;
    width:100vw;
    height:100vh;
    z-index: 1;
    background-color: black;
    opacity: 95%;

        &:hover{
            opacity:inherit;
        }

    button{
        font-size: 4rem;
        color: #ffffff;
    }

    .link-wrapper{
        position: relative;
        left: 700px;
        top: 250px;
        display: block;
        padding: 8px 0;

        a{
            font-size: 17px;
            text-transform: uppercase;
            color: #ffffff;
        }


        .link {
            display: inline-block;
            position: relative;
            text-decoration: none;
            padding: 10px 0;
            color: #fff;
        }

        .hover-2 { 

            &:after {
                content: '';
                position: absolute;
                bottom: 0;
                left: 0;
                right: 0;
                width: 100%;
                height: 3px;
                transform: scaleX(0);
                background-color: map-get($maps, golden);
        
                transition: transform 0.3s;
            }
        
            &:hover {
                &:after {
                    transform: scaleX(1);    
                }
            }
        }

    }

}
